import React, { useEffect, useState } from 'react'
import Navbar from '../components/Navbar'
import axios from 'axios'
import { Link ,useNavigate, useParams } from 'react-router-dom'
import Footer from '../components/Footer';
import UserSessionBreak from './UserSessionBreak';

function CompaniesCar() {
  axios.defaults.withCredentials = true;
  const navigate = useNavigate()
  const [getData,setGetData] = useState(false)
  console.log(getData+"asdsa")
  function parent(data){
    setGetData(data)
  }

 
  const params = useParams()

  const [carData,setCarData] = useState([])
  
  const fetchData = async () =>{
          try{
              const {data} = await axios.get(`http://localhost:8000/companies/${params.id}`)
              if(data.error){
                  navigate("/404")
              }
              else{
                  setCarData(data)
              }
          }catch(e){
              console.log(e)
          }
      }

  useEffect(()=>{
      fetchData()
  },[])
  return (
    <>
    <UserSessionBreak/>
    <Navbar alert={parent}/>
    <div className='bg-gray-50 pb-10'>
    <div class=" h-72 overflow-hidden bg-gray-900 sm:px-20">
    <div class="z-10 flex h-full flex-row items-center justify-center space-x-4">
    <div class="flex-1">
    <div className='flex flex-wrap justify-center '>
    <img src='/order.png' width="300px" className='hidden sm:block md:hidden lg:block' />
</div>
    </div>
    <div class="flex-1">
      
    <h1 class="text-center text-5xl font-bold text-white">Select Your Favourite Car</h1>
      
    </div>
    <div class="flex-1">
    <div className='flex flex-wrap justify-center '>
    <img src='/search.gif' width="300px" className='hidden sm:block md:hidden lg:block' />
</div>
    </div>
</div>



</div>


</div>
    <div className="flex flex-wrap justify-center bg-gray-50">
      
    {carData.map(user => (
  user.car.map(car => (
    <div key={car._id} class="relative m-2 flex w-full max-w-xs flex-col overflow-hidden rounded-lg border border-gray-100 bg-white shadow-md">
  <a class="relative mx-3 mt-3 flex h-60 overflow-hidden rounded-xl" href="#">
    <img class="object-cover" src={"http://localhost:8000/upload/"+car.carImage} alt="product image" />
    <span class="absolute top-0 left-0 m-2 rounded-full bg-black px-2 text-center text-sm font-medium text-white">
    {car.isAvailable ?   <span class="absolute top-0 left-0 m-2 rounded-full bg-green-500 px-2 text-center text-sm font-medium text-white">Available</span> :
              <span class="absolute top-0 left-0 m-2 rounded-full bg-red-500 px-2 text-center text-sm font-medium text-white">Rented</span> 
            }
    </span>
  </a>
  <div class="mt-4 px-5 pb-5">
    <a href="#">
      <h5 class="text-xl pb-2 font-sans font-serif font-mono  tracking-tight text-slate-900">{car.carName}</h5>
  
    </a>
    <hr/>
<ul className="box-border flex list-none items-center border-t border-b  border-gray-200 px-0 py-4">
          <li className="mr-4 flex items-center text-left">
            <i className="mr-2 text-2xl text-green-600">
            <img src="/speedometer.svg" className="h-5 w-5" />  
            </i>
            <span className="text-sm">{car.fuelEfficiency}km</span>
          </li>

          <li className="mr-4 flex items-center text-left">
            <i className="mr-2 text-2xl text-green-600">
              <img src="/icon.svg" className="h-4 w-4" />            </i>
            <span className="text-sm">{car.engine}</span>
          </li>

          <li className="flex items-center text-left">
            <i className="mr-2 text-2xl text-green-600">
            <img src="/car-seat.svg" className="h-5 w-5" />  
            </i>
            <span className="text-sm">{car.seatCapacity} Seats</span>
          </li>
        </ul>
        <hr/>
    <div class="mt-2 mb-3 flex items-center justify-between">
  <div class="flex items-center"> 
  <svg className="mt-1 w-6 h-6 fill-current" xmlns="http://www.w3.org/2000/svg" xmlSpace="preserve" viewBox="0 0 784.37 1277.39">
                <polygon className="fill-current text-gray-600" fillRule="nonzero" points="392.07,0 383.5,29.11 383.5,873.74 392.07,882.29 784.13,650.54 " />
                <polygon className="fill-current text-gray-400" fillRule="nonzero" points="392.07,0 -0,650.54 392.07,882.29 392.07,472.33 " />
                <polygon className="fill-current text-gray-700" fillRule="nonzero" points="392.07,956.52 387.24,962.41 387.24,1263.28 392.07,1277.38 784.37,724.89 " />
                <polygon className="fill-current text-gray-400" fillRule="nonzero" points="392.07,1277.38 392.07,956.52 -0,724.89 " />
                <polygon className="fill-current text-gray-900" fillRule="nonzero" points="392.07,882.29 784.13,650.54 392.07,472.33 " />
                <polygon className="fill-current text-gray-700" fillRule="nonzero" points="0,650.54 392.07,882.29 392.07,472.33 " />
              </svg>
    <span class="text-2xl font-bold text-slate-900">{car.price}<span class="text-sm text-slate-900 ">ETH</span></span> 
  </div>
  <div class="flex items-center">
  <p className="rounded-full mt-0.5 bg-green-200 py-0 px-2 text-sm font-sm text-green-700">{car.transmission}</p>
  </div>
  <div class="flex items-center">
    <span class="mr-2 ml-3 rounded bg-yellow-200 px-2.5 py-0.5 text-xs font-semibold">{car.carModel}</span>
  </div>
</div>


    <Link to={"/companies/"+user.companyName+"/"+car._id+"/book"} class="flex items-center justify-center rounded-md bg-slate-900 px-5 py-2.5 text-center text-sm font-medium text-white hover:bg-gray-700 focus:outline-none focus:ring-4 focus:ring-blue-300">
      
      Book Now</Link>
  </div>
</div>
))
))}
</div>
    <div className='pt-1 bg-gray-50'>
<Footer/>
</div>
    
    </>

  )
}

export default CompaniesCar

